import React from "react";
import './App.css';
import axios from "axios";
class App extends React.Component
{
  state={
   advice:' '
  };
  componentDidMount()
  {
    //to fetch actual advice we will use axios and fetchapi so we will create one function for that
    this.fetchAdvice();
  }
   fetchAdvice=()=>{
     axios.get('https://api.adviceslip.com/advice')
     .then((response)=>{
      const {advice}=response.data.slip; //equivalent to const advice = response.data.slip.advice;
      this.setState({advice});
     }
     )
     .catch((error)=>{
      console.log(error);
     }
     )
    }
  render(){
    const {advice}=this.state;
    return(

      <div className="container">
        <div className="card">
        <h1 className="heading"style={{ color: 'black', fontSize:'40px',fontFamily:'Nunito, sans-serif;'}}>{advice}</h1>
        <button type="button" class="btn btn-dark my-15"><span>Give me advice !</span></button>
        </div>
      </div>
    )
  }
}
export default App;
